import { Component, OnInit } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent implements OnInit {
  constructor(public authService: AuthService) {}

  ngOnInit() {
    this.getUserDetails();
  }

  async getUserDetails() {
    await this.authService.getUserDetails();
  }

  logout() {
    this.authService.logout();
  }
}
<div class="container d-flex justify-content-center align-items-center vh-100 w-100">
  <div class="row justify-content-center h-100 w-100">
    <div class="col-12 col-md-6 d-flex align-items-center">
      <div class="p-5 text-center">
        <h1 class="display-6 fw-bold">
          Welcome to your dashboard, {{ authService.user.username }} ðŸ˜€
        </h1>

        <button
          class="actionBtn"
          type="button"
          data-bs-toggle="modal"
          data-bs-target="#registerModal"
        >
          Log Out
        </button>
      </div>
    </div>
  </div>
</div>
